var Schema = mongoose.Schema

var pictogramSearchSchema = new Schema({
  searchText: String, 
  lastSearch: { type : Date, default : Date.now },
  count: Number
})


// method based on http://stackoverflow.com/questions/28891165/using-weights-for-searching-in-mongoose
