 * Grant implicit authorization.
 *
 * The callback takes the `client` requesting authorization, the authenticated
 * `user` granting access, and their response, which contains approved scope,
 * duration, etc. as parsed by the application.  The application issues a token,
 * which is bound to these values.
 */
server.grant(oauth2orize.grant.token(function (client, user, ares, done) {
  var token = utils.createToken({ sub: user.id, exp: config.token.expiresIn });
  var expiration = config.token.calculateExpirationDate();

  db.accessTokens.save(token, expiration, user.id, client.id, client.scope).then(function () {
    return done(null, token, expiresIn);
  }).catch(function (err) {
    return done(err);
  });
}));

/**
