    return validate.authCode(code, authCode, client, redirectURI);
  }).then(function (authCode) {
    return validate.generateTokens(authCode);
  }).then(function (tokens) {
    if (tokens.length === 1) {
      return done(null, tokens[0], null, expiresIn);
    }
    if (tokens.length === 2) {
      return done(null, tokens[0], tokens[1], expiresIn);
