  }).then(function (user) {
    return validate.generateTokens({ scope: scope, userID: user.id, clientID: client.clientId });
  }).then(function (tokens) {
    console.log('**********' + tokens + '******');
    if (tokens === false) {
      console.log('1');
      return done(null, false);
    }
    if (tokens.length === 1) {
      console.log('2');
      return done(null, tokens[0], null, expiresIn);
    }
    if (tokens.length === 2) {
      console.log('3');
      return done(null, tokens[0], tokens[1], expiresIn);
    }
