server.exchange(oauth2orize.exchange.clientCredentials(function (client, scope, done) {
  var token = utils.createToken({ sub: client.id, exp: config.token.expiresIn });
  var expiration = config.token.calculateExpirationDate();
  // Pass in a null for user id since there is no user when using this grant type
  db.accessTokens.save(token, expiration, null, client.id, scope).then(function () {
    return done(null, token, null, expiresIn);
  }).catch(function (err) {
    return done(err);
  });
