  console.log(username);
  User.findOne({ email: username }).then(user => user ? user.validate(password) : logAndThrow(`User ${username} not found`)).then(user => done(null, user)).catch(error => {
    console.log(`Login error: ${error.message}`);
    done(null, false);
  });
}));

