passport.use(new BasicStrategy((clientId, clientSecret, done) => {
  Client.findOne({ clientId: clientId }).then(client => client ? client.validate(clientSecret) : logAndThrow(`Client with id ${clientId} not found`)).then(client => done(null, client)).catch(error => {
